import React, { Component } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import xmlIcon from "../static/xml-icon.png";

class FileUploadButton extends Component {
  constructor(props) {
    super(props);
    this.state = { file: null };
  }
  render() {
    return (
      <div>
        <input
          accept="image/*"
          id="contained-button-file"
          type="file"
          onChange={file => this.setState({ file: file.target.files[0].name })}
          style={{ display: "none" }}
        />
        <label htmlFor="contained-button-file">
          <div style={style.button}>UPLOAD</div>
        </label>
        {this.state.file ? this.state.file : "no file uploaded"}
        <img src={xmlIcon} alt="icon" width={20} />
      </div>
    );
  }
}

const style = {
  button: {
    backgroundColor: "#00C6BF",
    width: "175px",
    height: "50px",
    borderRadius: "25px",
    boxShadow: "0px 0px 10px rgb(50, 50, 50)",
    textAlign: "center",
    verticalAlign: "middle",
    lineHeight: "50px",
    color: "white",
    fontSize: "15px"
  }
};

export default FileUploadButton;
