import React, { Component } from "react";
import Button from "./Button.js";
import FileDrop from "react-file-drop";
import "../static/fileDrop.css";
import MaterialIcon from "material-icons-react";

class Content extends Component {
  constructor(props) {
    super(props);
    this.state = { file: null, inFrame: false, inTarget: false };
  }
  render() {
    console.log(this.state.inTarget);
    return (
      <div
        style={{
          display: "flex",
          flexDirection: "column"
        }}
      >
        <div>
          <input
            accept="image/*"
            id="contained-button-file"
            type="file"
            onChange={file => console.log(file.target.files[0].name)}
            style={{ display: "none" }}
          />
          <label htmlFor="contained-button-file">
            <div style={style.fileDrop}>
              <FileDrop onDragOver={() => this.setState({ inTarget: true })}>
                <div style={style.center}>
                  <div style={style.fileDropContent}>
                    <MaterialIcon
                      icon="note_add"
                      color="white"
                      size={this.state.inTarget ? "large" : "medium"}
                    />
                    Drop file here or click to upload
                  </div>
                </div>
              </FileDrop>
            </div>
          </label>
          {this.state.file ? this.state.file : "no file uploaded"}
        </div>
      </div>
    );
  }
}

const style = {
  fileDrop: {
    width: "75vh",
    height: "40vh",
    border: "3px dashed white",
    color: "rgba(255,255,255,0.8)",
    borderRadius: "5px",
    backgroundColor: "#00456A",
    cursor: "pointer"
  },
  center: {
    position: "absolute",
    top: "50%",
    transform: "translateY(-50%)"
  },
  fileDropContent: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center"
  }
};

export default Content;
