import React, { Component } from "react";
import Button from "./Button.js";
import FileDrop from "react-file-drop";
import "../static/fileDrop.css";
import MaterialIcon from "material-icons-react";
import xmlIcon from "../static/xml-icon.png";

class Content extends Component {
  constructor(props) {
    super(props);
    this.state = { file: null };
  }

  handleFileChange = file => {
    this.setState({ file: file });
  };

  render() {
    return (
      <div
        style={{
          display: "flex",
          flexDirection: "column"
        }}
      >
        {!this.state.file ? (
          <div style={style.fileDrop}>
            <input
              accept="image/*"
              id="contained-button-file"
              type="file"
              onChange={file =>
                this.handleFileChange(file.target.files[0].name)
              }
              style={{ display: "none" }}
            />
            <label htmlFor="contained-button-file">
              <FileDrop>
                <div style={style.center}>
                  <div style={style.fileDropContent}>
                    <MaterialIcon icon="note_add" color="white" size="large" />
                    Drop file here or click to upload
                  </div>
                </div>
              </FileDrop>
            </label>
          </div>
        ) : (
          <div style={style.fileDropped}>
            <div style={style.center}>
              <img src={xmlIcon} alt="icon" width="30px" />
              <div>{this.state.file}</div>
            </div>
          </div>
        )}
        <div style={style.buttons}>
          <Button text="HELP" />
          <Button text="CONVERT" disabled={!this.state.file} />
        </div>
      </div>
    );
  }
}

const style = {
  fileDrop: {
    margin: "auto",
    width: "75vh",
    height: "40vh",
    border: "3px dashed white",
    color: "rgba(255,255,255,0.8)",
    borderRadius: "5px",
    backgroundColor: "#00456A",
    cursor: "pointer"
  },
  center: {
    position: "absolute",
    top: "50%",
    left: "50%",
    transform: "translate(-50%, -50%)"
  },
  fileDropContent: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center"
  },
  buttons: {
    margin: "50px",
    display: "flex",
    justifyContent: "space-around"
  },
  fileDropped: {
    margin: "auto",
    width: "75vh",
    height: "40vh",
    border: "3px dashed white",
    color: "rgba(255,255,255,0.8)",
    borderRadius: "5px",
    backgroundColor: "rgba(2, 10, 22, 0.75)",
    cursor: "pointer"
  }
};

export default Content;
